import React, {
    useEffect,
    useState,
    useReducer
} from 'react'
import Rest from '../rest'

const baseURL = 'https://mymoney-71935.firebaseio.com/'
const {
    useGet,
    usePost,
    useDelete
} = Rest(baseURL)

const Home = () => {
    const data = useGet('mes')


    return (
        <>
            {data.loading &&
                <div class="spinner-border text-info" role="status">
                    <span class="sr-only">Loading...</span>
                </div>
            }

            {!data.loading && (
                <table style={{ table: "fixed" }} className="table table-bordered table-info table-hover">
                    <thead>
                        <tr>
                            <th>Mês</th>
                            <th>Valor</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            Object
                                .keys(data)
                                .map(mes => {
                                    cnsole.log(mes)
                                    return (
                                        <tr key={mes}>
                                            <td>{mes}</td>
                                            <td>{data[mes].d01}</td>
                                        </tr>
                                    )
                                })
                        }
                    </tbody>
                </table>
            )
            }
        </>
    )
}

export default Home
