import React, {
    useEffect,
    useState,
    useReducer
} from 'react'
import Rest from '../rest'
import CurrencyInput from 'react-currency-input'

const baseURL = 'https://mymoney-71935.firebaseio.com/'
const {
    useGet,
    usePost,
    useDelete
} = Rest(baseURL)

const Home = () => {
    const data = useGet('mes')
    const saldo = useGet('saldo')
    const [form, setForm] = useState({})
    const today = new Date()

    const handleChange = field => (event, maskedvalue, floatvalue) => {
        setForm({
            ...form,
            [field]: maskedvalue
        })
    }

    return (
        <>
            <div className="header-tabs">
                <div style={{ color: "white", textAlign: "center" }}>
                    <h1>Adicionar/Remover dinheiro</h1>
                </div>
            </div>

            <div className="resize-calendar" style={{ marginLeft: "5%" }}>
                <br />

                {
                    saldo.data &&
                    Object
                        .keys(saldo.data)
                        .map(s => {
                            console.log("aaaa")
                            return (
                                <h1>Saldo atual: {s}</h1>
                            )

                        })
                }


                <form className="align-labels">
                    <div class="form-row align-items-end">
                        <div class="form-group col-md-3">
                            <label for="inputCity">Date</label>
                            <input
                                className="form-control"
                                type="date"
                                value={form.date}
                                onChange={e => { setForm({ date: e.target.value }); console.log(form.date) }}
                            />
                        </div>
                        <div class="form-group col-md-3">
                            <button type="button" onClick={() => setForm({
                                date: today.getFullYear() + '-' + ((today.getMonth() + 1) < 10 ? '0' + (today.getMonth() + 1) : (today.getMonth() + 1)) +
                                    '-' + (today.getDate() < 10 ? '0' + today.getDate() : today.getDate())
                            }
                            )}
                                className="btn btn-primary form-control" >Get Current Date</button>
                        </div>
                        <div class="form-group col-md-3">
                            <label for="inputState">Time</label>
                            <input
                                className="form-control"
                                type="time"
                                value={form.time}
                                onChange={e => { setForm({ time: e.target.value }); console.log(form.time) }}
                            />
                        </div>
                        <div class="form-group col-md-3">
                            <button type="button" onClick={() => { setForm({ time: today.getHours() + ":" + today.getMinutes() }); console.log(today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate()) }}
                                className="btn btn-primary form-control" >Get Current Time</button>
                        </div>
                    </div>
                    <div class="form-row">
                        <div class="form-group col-md-3">
                            <label for="inputEmail4">Opera√ß√£o</label>
                            <select type="select" class="form-control">
                                <option>...</option>
                                <option>+</option>
                                <option>-</option>
                            </select>
                        </div>
                        <div class="form-group col-md-9">
                            <label for="inputPassword4">Quantidade</label>
                            <CurrencyInput prefix='R$' onChange={handleChange('salario')} value={form.salario} className='form-control input-color' id='inputSalario' placeholder='Sal√°rio' />
                        </div>
                    </div>
                </form>

                <button style={{ marginTop: "2%", fontSize: "2em", width: "9em", height: "4em" }}
                    type="button" className="btn btn-primary" >Registrar movimenta√ß√£o</button>
            </div>
        </>
    )
}

export default Home
