import React, {
    useEffect,
    useState,
    useReducer
} from 'react'
import Rest from './rest'
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'

const baseURL = 'https://mymoney-71935.firebaseio.com/'
const {
    useGet,
    usePost,
    useDelete
} = Rest(baseURL)

function App() {
    const data = useGet('movimentacoes')
    const [postData, post] = usePost('movimentacoes/2020-04')
    const [deleteData, remove] = useDelete()
    const [sidebarActive, setSidebarActive] = useState(0)

    const salvar = () => {
        post({
            valor: 10,
            descricao: 'ola'
        })
    }

    const remover = () => {
        remove('movimentacoes/2020-04/-M6-hV-ETYC0dcs9Hxpa')
    }


    return (
        <div class="wrapper">

            <nav id="sidebar">
                <div id="dismiss">
                    <i class="fas fa-arrow-left"></i>
                </div>

                <div style={{ textAlign: "center" }} class="sidebar-header">
                    <h3>
                        <img style={{ width: 120, height: 100 }} src="https://imagepng.org/wp-content/uploads/2019/05/dinheiro-icone.png" alt="img" /> <br />
                        My Money
                    </h3>
                </div>

                <ul class="list-unstyled components">
                    <p>Bem vindo!</p>
                    <li class={sidebarActive === 0 ? "active" : ""}>
                        <a onClick={() => setSidebarActive(0)} href="#homeSubmenu" data-toggle="collapse" aria-expanded="false">Home</a>
                    </li>
                    <li class={sidebarActive === 1 ? "active" : ""}>
                        <a onClick={() => setSidebarActive(1)} href="#">Calendar</a>
                    </li>
                </ul>
            </nav>


            <div style={{ display: "flex", backgroundColor: "#b0c0e0", flexDirection: "column", width: "100%" }}>

                <div className="header-tabs">
                    <div style={{ color: "white", textAlign: "center" }}>
                        <h1>Calendar</h1>
                    </div>
                </div>

                <div className="resize-calendar" style={{ marginLeft: "5%" }}>
                    <br />
                    <div>
                        <FullCalendar
                            defaultView="dayGridMonth"
                            plugins={[dayGridPlugin]}
                            weekends={true}
                            events={[
                                { title: 'event 1', date: '2020-04-30' },
                                { title: '+ R$ 400000,00', date: '2020-04-29' }
                            ]}
                        />
                    </div>

                    <br />
                </div>



            </div>


        </div >
    )
}

export default App
