import React, {
    useEffect,
    useState
} from 'react'
import Rest from './rest'
import $ from 'jquery'

const baseURL = 'https://mymoney-71935.firebaseio.com/'
const {
    useGet,
    usePost,
    useDelete
} = Rest(baseURL)

function App() {
    const data = useGet('movimentacoes')
    const [postData, post] = usePost('movimentacoes/2020-04')
    const [deleteData, remove] = useDelete()

    const salvar = () => {
        post({
            valor: 10,
            descricao: 'ola'
        })
    }

    const remover = () => {
        remove('movimentacoes/2020-04/-M6-hV-ETYC0dcs9Hxpa')
    }

    

        $('#sidebarCollapse').on('click', function () {
            $('#sidebar').toggleClass('active');
        });
    
   

    return (

        
            <div class="wrapper">
                <nav id="sidebar">
                    <div class="sidebar-header">
                        <h3>Bootstrap Sidebar</h3>
                    </div>

                    <ul class="list-unstyled components">
                        <p>Dummy Heading</p>
                        <li class="active">
                            <a href="#homeSubmenu" data-toggle="collapse" aria-expanded="false" class="dropdown-toggle">Home</a>


                        </li>
                        <li>
                            <a href="#">About</a>
                        </li>
                        <li>
                            <a href="#pageSubmenu" data-toggle="collapse" aria-expanded="false" class="dropdown-toggle">Pages</a>

                        </li>
                        <li>
                            <a href="#">Portfolio</a>
                        </li>
                        <li>
                            <a href="#">Contact</a>
                        </li>
                    </ul>

                </nav>
                <div className="bg-primary" id="content">

                    <nav class="navbar bg-primary navbar-expand-lg navbar-light bg-light">
                        <div class="container-fluid">

                            <button type="button" id="sidebarCollapse" class="btn btn-info">
                                <span>Toggle Sidebar</span>
                            </button>
                        </div>
                    </nav>
                </div>
            </div>


        



    )
}

export default App
