import React, {
    useEffect,
    useState,
    useReducer
} from 'react'
import Rest from '../rest'
import InputMask from 'react-input-mask'

const baseURL = 'https://mymoney-71935.firebaseio.com/'
const {
    useGet,
    usePost,
    useDelete
} = Rest(baseURL)

const Home = () => {
    const data = useGet('mes')
    const [date, setDate] = useState('__/__/____')


    return (
        <>
            <div className="header-tabs">
                <div style={{ color: "white", textAlign: "center" }}>
                    <InputMask value={date} onChange={e => setDate(e.target.value)} mask="99/99/9999" maskChar="_" ></InputMask>
                </div>
            </div>
        </>
    )
}

export default Home
